import "./App.css";
import * as BookAPI from "./BooksAPI";
import {useState, useEffect, forceUpdate} from "react";
import {Routes, Route} from "react-router-dom"
import Read from "./Read.js"

function App() {
  const [showSearchPage, setShowSearchpage] = useState(false);

  const [currentlyReading, setCurrentlyReading] = useState([])
  const [wantToRead, setWantToRead] = useState([])
  const [read, setRead] = useState([])

  const updateBookShelf = (book,shelf) => {
      BookAPI.update(book,shelf)
      this.forceUpdate()

  }


  useEffect(()=>{




      const getBooks = async () => {
        const result = await BookAPI.getAll()
        console.log("triggered")



        result.map((data) => {



          // console.log(data)

            if(data.shelf === "currentlyReading"){
              setCurrentlyReading(currentlyReadin => [...currentlyReadin, data])

            }
            else if(data.shelf === "wantToRead"){
              setWantToRead(wantToRead => [...wantToRead, data])
            }
            else if(data.shelf === "read"){
              setRead((read) => [...read, data])
            }

          })




    }



   getBooks


  })



  return (

        <div className="app">
          <div className="list-books">
              <div className="list-books-title">
                <h1>MyReads</h1>
              </div>
                {(currentlyReading) && <Read title="Currently Reading" update={updateBookShelf} books={currentlyReading}></Read>
                }
                  {(wantToRead) && <Read title="Want to read" update={updateBookShelf} books={wantToRead}></Read>
                }
                  {(read) && <Read title="Read" update={updateBookShelf} books={read}></Read>
                }


            </div>
        </div>

  );
}

export default App;
