const Book = ({book , update}) => {



    const setShelf = (shelf) => {
       update(book, shelf)

    }




    return(
        <li key={book.title}>
        <div className="book">
          <div className="book-top">
            <div
              className="book-cover"
              style={{
                width: 128,
                height: 193,
                backgroundImage:
                  `url("${book.imageLinks !== undefined && book.imageLinks.smallThumbnail}")`,
              }}
            ></div>
            <div className="book-shelf-changer">
              <select value={() => {book.shelf !== undefined? book.shelf: "None"}} onChange={(event) => {setShelf(event.target.value)}}>
                <option value="none" disabled>
                  Move to...
                </option>
                <option  value="currentlyReading">
                  Currently Reading
                </option>
                <option value="wantToRead">Want to Read</option>
                <option value="read">Read</option>
                <option value="none">None</option>
              </select>
            </div>
          </div>
          <div className="book-title">{book.title}</div>

          <div className="book-authors">{ book.authors !== undefined && (book.authors).map((author) =>  <p key={author}>{author}</p>)} 
        </div> 
        </div>
      </li>
    );

}

export default Book;
