const Book = ({book , update}) => {



    const setShelf = (shelf) => {
       update(book, shelf)

    }



    return(
        <li>
        <div className="book">
          <div className="book-top">
            <div
              className="book-cover"
              style={{
                width: 128,
                height: 193,
                backgroundImage:
                  `url("${book.imageLinks !== null && book.imageLinks.smallThumbnail}")`,
              }}
            ></div>
            <div className="book-shelf-changer">
              <select value={book.shelf} onChange={(event) => {setShelf(event.target.value)}}>
                <option value="none" disabled>
                  Move to...
                </option>
                <option  value="currentlyReading">
                  Currently Reading
                </option>
                <option value="wantToRead">Want to Read</option>
                <option value="read">Read</option>
                <option value="none">None</option>
              </select>
            </div>
          </div>
          <div className="book-title">{book.title}</div>
          <div className="book-authors">{ book.authors !== null && 
          book.authors.map((result) => (<p>{result}</p> ))}</div> {console.log(book.title+" "+book.authors)}
        </div>
      </li>
    );

}

export default Book;
