import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import Book from "./Book";
import * as BooksAPI from "./BooksAPI"

const Search = ({update, whichShelfIsBookLocatedAt}) => {

  const [entry,setEntry] = useState("")

  const [books, setBooks] = useState([])

  const isEntryEmpty = entry.length === 0;



  useEffect(() => {

    let mounted = true

    if(!isEntryEmpty && mounted){
    const search = async () => {
      const result = await BooksAPI.search(entry,50);
      setBooks(result) 


    }


    search()
    console.log(books)
  }
  else if(entry == ""){
      setBooks([])
  }


  return (() => {
    mounted = false;
    setBooks([])  
  })

  },[entry])



    return(
      <div className="search-books">
        <div className="search-books-bar">
          <Link
            className="close-search"
            to="/"
          >
            Close
          </Link>
          <div className="search-books-input-wrapper">
            <input
              type="text"
              onChange={(event) => {setEntry(event.target.value)}
              }
              placeholder="Search by title, author, or ISBN"
            />
          </div>
        </div>
        <div className="search-books-results">
          <ol className="books-grid">
          { books.length > 0 &&
           books.map((book,index) => (
           <Book whichShelfIsBookLocatedAt={whichShelfIsBookLocatedAt} key={index } book={book} update={update}></Book> )
          )
          }
          </ol>
        </div>
      </div>);
}

