import "./App.css";
import * as BookAPI from "./BooksAPI";
import {useState, useEffect, forceUpdate} from "react";
import { Route, Routes, Link } from "react-router-dom";
import Read from "./Read.js"
import Search from "./Search";

function App() {
  const [showSearchPage, setShowSearchpage] = useState(false);

  const [currentlyReading, setCurrentlyReading] = useState([])
  const [wantToRead, setWantToRead] = useState([])
  const [read, setRead] = useState([])
  const [updated, setUpdated] = useState(true)

  const deleteAllData = () => {
    setCurrentlyReading([])
    setRead([])
    setWantToRead([])
  }

  const removeBook = (book,shelf) => {


  }



  const updateBookShelf = async (book,shelf,previousShelf) => {
     await BookAPI.update(book,shelf)



    switch(previousShelf){
      case "wantToRead": 
        var index = wantToRead.indexOf(book)
        if(index!==-1){
          setWantToRead([wantToRead.splice(index,1)])
        };
        break;
      case "read":   
        var index = read.indexOf(book)
      if(index!==-1){
        setWantToRead([read.splice(index,1)])
      };
      break;
      case "currentlyReading":   
      var index = currentlyReading.indexOf(book)
    if(index!==-1){
      setWantToRead([currentlyReading.splice(index,1)])
    };
    break;
    }

      setUpdated(true);


  }


  useEffect(()=>{

    if(updated){

      const getBooks = async () => {
        const result = await BookAPI.getAll()
        console.log(result)


        result.map((data) => {


          if(data.shelf === "currentlyReading"){
            if(!currentlyReading.includes(data))
            setCurrentlyReading(currentlyReadin => [...currentlyReadin, data])

          }
          if(data.shelf === "wantToRead"){
            if(!wantToRead.includes(data))
            setWantToRead(wantToRead => [...wantToRead, data])
          }
          if(data.shelf === "read"){
            if(!read.includes(data))
            setRead((read) => [...read, data])
          }


        })


      }
      getBooks();
    }
    return (() => { setUpdated(false);
    })






  },[updated])




  return (
    <div className="app">

              <Routes>
                <Route path="" element={
                  <div>
                    <div className="list-books">
                      <div className="list-books-title">
                        <h1>MyReads</h1>
                      </div>
                    {(currentlyReading.length > 0) && <Read title="Currently Reading" update={updateBookShelf} books={currentlyReading}></Read>
                    }

                    {(wantToRead.length > 0) && <Read title="Want to read" update={updateBookShelf} books={wantToRead}></Read>
                    }
                    {(read.length > 0) && <Read title="Read" update={updateBookShelf} books={read}></Read>}
                  </div>
                  </div>


                }/>
  <Route path="/Search" element={
                  <div>
                  <Search></Search>
                  </div>

                }/>

            </Routes>

            <div className="open-search">
            <Link to="/Search">Add a book</Link>
            </div>

        </div>

  );
}

export default App;
