const Book = ({book , update}) => {

    var select = document.getElementById("shelf")

    shelf.addEventListener('change', () => {
        update(book, this.value)
    })

    const setShelf = ({book, newShelf}) => {
        update(book, newShelf);
    }


    return(
        <li>
        <div className="book">
          <div className="book-top">
            <div
              className="book-cover"
              style={{
                width: 128,
                height: 193,
                backgroundImage:
                  `url("${book.imageLinks.smallThumbnail}")`,
              }}
            ></div>
            <div className="book-shelf-changer">
              <select id="shelf">
                <option value="none" disabled>
                  Move to...
                </option>
                <option  value="currentlyReading">
                  Currently Reading
                </option>
                <option value="wantToRead">Want to Read</option>
                <option value="read">Read</option>
                <option value="none">None</option>
              </select>
            </div>
          </div>
          <div className="book-title">{book.title}</div>
          <div className="book-authors">{book.authors.map((result) => {<p>{result}</p> })}</div>
        </div>
      </li>
    );

}

export default Book;
